<?phpnamespace App\Model;class ContactManager extends AbstractManager{    const TABLE = 'item';    public function __construct()    {        parent::__construct(self::TABLE);    }    public function update($id, $titre)    {        $statement = $this->pdo->prepare("UPDATE $this->table SET `title` = :title WHERE id=:id");        $statement->bindValue('id', $id, \PDO::PARAM_INT);        $statement->bindValue('title', $titre, \PDO::PARAM_STR);        $statement->execute();    }    public function insert(string $titre)    {        $statement = $this->pdo->prepare("INSERT INTO $this->table (`title`) VALUES (:title)");        $statement->bindValue('title', $titre, \PDO::PARAM_STR);        $statement->execute();    }    public function delete(int $id): void    {        // prepared request        $statement = $this->pdo->prepare("DELETE FROM $this->table WHERE id=:id");        $statement->bindValue('id', $id, \PDO::PARAM_INT);        $statement->execute();    }}